<style>
.dress{
    width:70px;
    height:70px;
}
.heart {
    animation: 2.5s ease 0s infinite beat;
}

@keyframes beat {

    /*Animacion del corazon*/
    0%,
    50%,
    100% {
        transform: scale(1, 1);
    }

    30%,
    80% {
        transform: scale(0.92, 0.95);
    }
}
.circulo {
    animation: drawCircle 3s infinite;
}
@keyframes drawCircle {
    0% {
        opacity: 0;
        transform: translateY(0);
      }
      20% {
        opacity: 1;
        transform: translateY(0);
      }
      80% {
        opacity: 0;
        transform: translateY(0);
      }
}
</style>